import UIKit

protocol SplashScreenPresentationLogic {
    func presentView(response: SplashScreen.PrepareView.Response)
}

class SplashScreenPresenter: SplashScreenPresentationLogic {
    weak var viewController: SplashScreenDisplayLogic?

    // MARK: Do something

    func presentView(response: SplashScreen.PrepareView.Response) {
        let viewModel = SplashScreen.PrepareView.ViewModel()
        viewController?.displayView(viewModel: viewModel)
    }
}
